css:
@media print {
    .page {
        border: 2px solid #000;
        padding: 20px;
        margin: 0;
        page-break-after: always; /* Ensures a new border on each page */
        position: relative;
        height:100%;
    }

    .page:last-child {
        page-break-after: auto; /* Removes page break after the last page */
    }
}

        .page-content {
            padding: 7px;
        }

        .table-container {
            display: flex;
            flex-direction: column;
            width: 100%;
            border: 1px solid #000;
            margin: 0;
            padding: 0;
        }

        .table-header,
        .table-row {
            display: flex;
            flex-direction: row;
            width: 100%;
            border-bottom: 1px solid #000;
        }

        .table-div {
            flex: 1;
            border: 1px solid #000;
            padding: 2px;
            border-bottom-width: 0;
        }

        .table-header .table-div {
            font-weight: bold;
        }

        .table-row:nth-child(even) {
            background-color: #f9f9f9;
        }
