@media print {
    /* Ensure the page has a full border around the entire page, regardless of content */

    .page-break { 
        border: 2px solid #000;
        padding: 20px;
        height: 100vh;
        margin: 0;
        position: relative;
        box-sizing: border-box;
        page-break-after: always;
    }

    .page-break:last-child {
        page-break-after: auto; 
    }

    /* Make sure content fits properly inside each page */
    .page-content {
        page-break-inside: auto;
        padding: 7px;
    }

    /* Ensure the table and its rows can break across multiple pages */
    .table-container {
        display: flex;
        flex-direction: column;
        width: 100%;
        margin: 0;
        padding: 0;
        page-break-after:always;
        page-break-inside: auto;
    }

    .table-header,
    .table-row {
        display: flex;
        flex-direction: row;
        width: 100%;
        border-bottom: 1px solid #000;
        page-break-inside: avoid; /* Prevents splitting a row over two pages */
    }

    /* Style for table cells */
    .table-div {
        flex: 1;
        border: 1px solid #000;
        padding: 2px;
        border-bottom-width: 0;
    }

    .table-header .table-div {
        font-weight: bold;
    }

    .table-row:nth-child(even) {
        background-color: #f9f9f9;
    }
}
