css:
.table {
    width: 100%;
    border-collapse: collapse;
}

/*.table-sm th, .table-sm td {*/
/*    padding: 0.5rem;*/
/*}*/

.table-bordered {
    border: 1px solid #dee2e6;
}
tr {
    height:10px !important;
}
.table-bordered th, .table-bordered td {
    border: 1px solid #dee2e6;
}

th, td {
    text-align: left;
    vertical-align: middle;
    padding:0px !important;
    margin:0px !important;
    height: 10px !important;
    /*width: fit-content;*/
}
.table-div{
    flex: 1; 
    border: 1px solid #000; 
    padding: 2px;
    border-bottom-width: 0px;
}

th {
    font-weight: bold;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">	</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="border: 2px solid #000; height: 100%;">
    <!-- First Section -->
    <div style="display: flex; justify-content: space-between; padding-top: 8px;padding-left:7px;padding-right:5px; border-top: 2px solid #000; width: 100%; margin-top: -2px;">
        <div>
            <strong>Lot Number:</strong> {{ doc.name }}
        </div>
        <div>
            <strong>Delivery Date:</strong> 
            {% if doc.expected_delivery_date %}
                {{ doc.expected_delivery_date }}
            {% endif %}
        </div>
    </div>

    <!-- Info Section -->
    <div style="padding: 7px; text-align: left;">
        <strong>Item:</strong> {{ doc.item }}
    </div>
    <div style="padding: 7px; text-align: left;">
        <strong>Tech Pack Version Number:</strong> 
        {% if doc.tech_pack_version %}
            {{ doc.tech_pack_version }}
        {% endif %}
    </div>
    <div style="padding: 7px; text-align: left;">
        <strong>Pattern Version Number:</strong> 
        {% if doc.pattern_version %}
            {{ doc.pattern_version }}
        {% endif %}    
    </div>


    <!-- Lot Order Details Section -->
    <div style="padding: 10px; border-top: 2px solid #000; margin-top: -2px;">
        {% set lot_order_details = get_dict_object(doc.lot_order_details_json) %}
        <!-- Outer table with collapsed borders -->
             <div style="display: flex; flex-direction: column; width: 100%; border: 1px solid #000; margin: 0; padding: 0;">
            {% if lot_order_details.primary_attribute %}
                <div style="display: flex; flex-direction: row; border-bottom: 1px solid #000;">
                    <div class='table-div'>S.No.</div>
                    {% for i in lot_order_details.final_state_attr %}
                        <div class='table-div'>{{ i }}</div>
                    {% endfor %}
                    {% for i in lot_order_details.primary_attribute_values %}
                        <div class='table-div'>{{ i }}</div>
                    {% endfor %}
                </div>

                {% for i in lot_order_details['items'] %}
                    <div style="display: flex; flex-direction: row; border-bottom: 1px solid #000;">
                        <div class='table-div' >{{ loop.index }}</div>
                        {% for j in i['attributes'] %}
                            <div style="flex: 1; border: 1px solid #000; padding: 2px;">{{ i['attributes'][j] }}</div>
                        {% endfor %}
                        {% for j in i['values'] %}
                            <div style="flex: 1; border: 1px solid #000; padding: 2px;">{{ i['values'][j] }}</div>
                        {% endfor %}
                    </div>
                {% endfor %}

            {% else %}
                <div style="display: flex; flex-direction: row; border-bottom: 1px solid #000;">
                    <div class='table-div'>S.No.</div>
                    {% for i in lot_order_details.final_state_attr %}
                        <div class='table-div'>{{ i }}</div>
                    {% endfor %}
                    <div class='table-div'>Quantity</div>
                </div>

                {% for i in lot_order_details['items'] %}
                    <div style="display: flex; flex-direction: row; border-bottom: 1px solid #000;">
                        <div class='table-div'>{{ loop.index }}</div>
                        {% if lot_order_details.final_state_attr %}
                            {% for j in i['attributes'] %}
                                <div class='table-div'>{{ i['attributes'][j] }}</div>
                            {% endfor %}
                        {% endif %}
                        <div class='table-div'>{{ i['values']['qty'] }}</div>
                    </div>
                {% endfor %}
            {% endif %}
        </div>

    </div>

    <!-- Total Quantity and Terms Section -->
    <div style="padding: 7px; text-align: left; border-top: 2px solid #000; margin-top: -2px;">
        <strong>Total Quantity:</strong> {{ doc.total_order_quantity }}
    </div>
    <div style="padding: 7px; text-align: left;">
        <strong>UOM:</strong> {{ doc.packing_uom }}
    </div>
    <div style="padding: 7px; border-top: 2px solid #000; margin-top: -2px; text-align: center;font-size:10px;">
        {% set attributes, data = get_attributes(doc.items) %}
        <table style="border: 1px solid #000; padding: 5px; margin: 0 auto;">
            {% for attr in attributes %}
            <tr style="border: 1px solid #000; padding: 5px;font-size:14px;">
                <td style="border: 1px solid #000; padding: 5px;font-size:14px;"><strong>{{ attr }}</strong></td>
                {% for d in data %}
                    <td style="border: 1px solid #000; padding: 5px;font-size:14px;">{{ d[attr] }}</td>
                {% endfor %}
            </tr>    
            {% endfor %}
        </table>
    </div>
    <!-- Terms and Conditions Section -->
    <div style="padding: 7px; text-align: left; border-top: 2px solid #000; margin-top: -2px;">
        <h3>Terms & Conditions</h3>
        <ul style="text-align: left;">
            <li>Excess Quantity 5% of Shipment Allowed</li>
            {% if doc.packing_uom != doc.uom%}
                <li>{{ doc.packing_combo }} {{doc.packing_uom}} per {{doc.uom}}</li>
            {% endif %}    
        </ul>
    </div>
    <!-- Signature Section -->
    <div style="display: flex; justify-content: space-between; padding-top: 30px;">

        <!-- Left Signature -->
        <div style="text-align: left; padding: 2px;">
            <span>Confirmed and Accepted by</span><br>
            <span>ESSDEE KNITTING MILLS PRIVATE LIMITED</span><br><br><br>
            <hr style="width: auto; margin-top: 5px; border: 0; border-top: 1px solid #000;">
            <span>Authorised Signature</span>
        </div>

        <!-- Right Signature -->
        <div style="text-align: right; padding: 2px;">
            <span>For and on behalf of</span><br>
            <span>ESSDEE KNITTING MILLS (P) LTD</span><br><br><br>
            <hr style="width: auto; margin-top: 5px; border: 0; border-top: 1px solid #000;">
            <span>Authorised Signature</span>
        </div>
    </div>

</div>
"}]
