css:
#background {
	position: absolute;
	z-index: 0;
	min-height: 143%;
	min-width: 93%;
}

#content {
	z-index: 1;
}

#bg-text {
	color: black;
	font-size: 120px;
	transform: rotate(315deg);
	-webkit-transform: rotate(315deg);
	opacity: 24%;
	text-align: center;
}

#small-bg-text {
	font-size: 20px;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="border:1px solid #aaa; border-radius: 0px; border-collapse: collapse;" class="container-fluid">
    {% if doc.docstatus == 0 or doc.docstatus == 2 %}
    <div id="background">
        <p id="bg-text">
            {% if doc.docstatus == 0 %}
            Draft
            <br> 
            <span class="small-bg-text" style="font-size: 20px;">This Document is pending for approval</span>
            {% else %}
            Cancelled
            <br> 
            <span class="small-bg-text" style="font-size: 20px;">This Document is Cancelled</span>
            {% endif %}
        </p>
    </div>
    {% endif %}
    <!-- <div class="row">
        <h2 class="text-center">
            PURCHASE ORDER
        </h2>
        <div class="row" style="padding-top:4px; border-top:1px solid #d1d8dd;">
        </div>
    </div> -->
    <div id="content">
        <div class="row" style="border-bottom:1px solid #666;">
            <div class="col-xs-8" style="border-right:1px solid #ddd;">
                <div class="col-xs-12" style="font-size:95%; padding-left: 0;">
                    <small>To:</small>
                    <br><strong>{{doc.supplier_name}}</strong>
                    <br>{{doc.supplier_address_display}}
                </div>
            </div>
            <div class="col-xs-4" style="border-left:1px solid #ddd;">
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="text-center">
                        <h4 style="margin: 0;">PURCHASE ORDER</h4>
                        {% if doc.docstatus == 0 %}
                        (Draft)
                        {% elif doc.docstatus == 2 %}
                        (Cancelled)
                        {% endif %}
                    </div>
                </div>
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-4 text-right" style=" padding-right: 4px; padding-left:0px;"><small>Date:</small></div>
                    <div class="col-xs-8" style="padding-left:0px;"><strong>{{doc.get_formatted("po_date")}}</strong></div>
                </div>
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-4 text-right" style=" padding-right: 4px; padding-left:0px;"><small>No.:</small></div>
                    <div class="col-xs-8" style="padding-left:0px;"><strong>{{doc.name}}</strong></div>
                </div>
                <br>
            </div>
        </div>
        <div class="row" style="border-bottom:1px solid #666;">
            <div class="col-xs-12 text-center">Particulars</div>
        </div>
        <div class="row">
            {% set item_details = fetch_item_details(doc.items) %}
            <table class="table table-sm table-bordered">
                {% for i in item_details %}
                <tr>
                    {% if i.primary_attribute %}
                    <td>
                        {% set show_discount = check_key_value_in_dict_or_list_of_dict('discount_percentage', i['items']) %}
                        <table class="table table-sm table-bordered">
                            <tr>
                                <th>S.No.</th>
                                <th>Item</th>
                                {% for attr in i['attributes'] %}
                                <th>{{ attr }}</th>
                                {% endfor %}
                                {% for attr in i['primary_attribute_values'] %}
                                <th>{{ attr }}</th>
                                {% endfor %}
                                {% if show_discount %}
                                <th>Discount</th>
                                {% endif %}
                                <th>Amount</th>
                            </tr>
                            {% for j in i['items'] %}
                            <tr>
                                {% if (doc.show_delivery_details and (j.delivery_location or j.delivery_date)) or j.comments %}
                                <td rowspan="2">{{ loop.index }}</td>
                                <td rowspan="2">{{ j.name }}</td>
                                {% else %}
                                <td>{{ loop.index }}</td>
                                <td>{{ j.name }}</td>
                                {% endif %}
                                {% for attr in i['attributes'] %}
                                <td>{{ j.attributes[attr] }}</td>
                                {% endfor %}
                                {% set total_amount = namespace(value=0) %}
                                {% for attr in j['values'] %}
                                <td>
                                    {% if j['values'][attr].qty %}
                                    <div>
                                        {{ j['values'][attr].qty }}{% if j.default_uom %}{{' ' + j.default_uom}}{% endif %}
                                        {% if j['values'][attr].secondary_qty %}
                                        <span>
                                            <br>
                                            ({{ j['values'][attr].secondary_qty }}{% if j.secondary_uom %}{{' ' + j.secondary_uom }}{% endif %})
                                        </span>
                                        {% endif %}
                                        <br>
                                        Rate: {{ frappe.utils.fmt_money(j['values'][attr].rate, currency="₹") }}
                                        <br>
                                        {{ j['values'][attr].tax }}% Tax
                                        {% set total_amount.value = total_amount.value + (j['values'][attr].qty * j['values'][attr].rate) %}
                                    </div>
                                    {% else %}
                                    <div class="text-center">
                                        ---
                                    </div>
                                    {% endif %}
                                </td>
                                {% endfor %}
                                {% if show_discount %}
                                <td>
                                    {{ j.discount_percentage }}%
                                </td>
                                {% endif %}
                                <td>
                                    {% set discount_amount = total_amount.value * (1 - ((j.discount_percentage or 0)/100)) %}
                                    {{ frappe.utils.fmt_money(discount_amount, currency="₹") }}
                                </td>
                            </tr>
                            {% if (doc.show_delivery_details and (j.delivery_location or j.delivery_date)) or j.comments %}
                            <tr>
                                {% if doc.show_delivery_details %}
                                {% set supplier = frappe.db.get_value("Supplier", j.delivery_location, "supplier_name") %}
                                <td><strong> Delivery Location:</strong><br>{{ supplier }}</td>
                                <td><strong> Delivery Date:</strong><br>{{ j.delivery_date }}</td>
                                {% endif %}
                                <td colspan="100"><strong> Comments:</strong><br>{{ j.comments }}</td>
                            </tr>
                            {% endif %}
                            {% endfor %}
                        </table>
                    </td>
                    {% else %}
                    <td>
                        {% set show_discount = check_key_value_in_dict_or_list_of_dict('discount_percentage', i['items']) %}
                        {% set show_comments = check_key_value_in_dict_or_list_of_dict('comments', i['items']) %}
                        <table class="table table-sm table-bordered">
                            <tr>
                                <th>S.No.</th>
                                <th>Item</th>
                                {% for attr in i.attributes %}
                                    <th>{{ attr }}</th>
                                {% endfor %}
                                <th>Quantity</th>
                                <th>Rate</th>
                                {% if show_discount %}
                                <th>Discount</th>
                                {% endif %}
                                {% if doc.show_delivery_details %}
                                <th>Delivery Location</th>
                                <th>Delivery Date</th>
                                {% endif %}
                                <th>Amount</th>
                                {% if show_comments %}
                                <th>Comments</th>
                                {% endif %}
                            </tr>
                            {% for j in i['items'] %}
                            <tr>
                                <td>{{ loop.index }}</td>
                                <td>{{ j.name }}</td>
                                {% for attr in i['attributes'] %}
                                    <td>{{ j.attributes[attr] }}</td>
                                {% endfor %}
                                <td>
                                    {{ j['values']['default'].qty }}{% if j.default_uom %}{{' ' + j.default_uom}}{% endif %}
                                    {% if j['values']['default'].secondary_qty %}
                                    <span v-if="j['values']['default'].secondary_qty">
                                        <br>
                                        ({{ j['values']['default'].secondary_qty }}{% if j.secondary_uom %}{{' ' + j.secondary_uom }}{% endif %})
                                    </span>
                                    {% endif %}
                                </td>
                                <td>
                                    {{ frappe.utils.fmt_money(j['values']['default'].rate, currency="₹") }}
                                    <br>
                                    {{ j['values']['default'].tax }}% Tax
                                </td>
                                {% if show_discount %}
                                <td>
                                    {{ j.discount_percentage }}%
                                </td>
                                {% endif %}
                                {% if doc.show_delivery_details %}
                                {% set supplier = frappe.db.get_value("Supplier", j.delivery_location, "supplier_name") %}
                                <td>{{ supplier }}</td>
                                <td>{{ j.delivery_date }}</td>
                                {% endif %}
                                {% set total_amount = j['values']['default'].qty * j['values']['default'].rate %}
                                {% set discount_amount = total_amount * (1 - ((j.discount_percentage or 0)/100)) %}
                                <td>{{ frappe.utils.fmt_money(discount_amount, currency="₹")  }}</td>
                                {% if show_comments %}
                                <td colspan="100">{{ j.comments }}</td>
                                {% endif %}
                            </tr>
                            {% endfor %}
                        </table>
                    </td>
                    {% endif %}
                </tr>
                {% endfor %}
            </table>
        </div>
        <div class="row" style="border-bottom:1px solid #666;">
            {% if doc.comments %}
            <div class="col-xs-12">
                PO Comments: {{doc.comments}}
            </div>
            {% endif %}
        </div>
        <div class="col-xs-12" style="border-bottom:1px solid #666;">
            <div class="row">
                <div class="col-xs-3"></div>
                <div class="col-xs-9">
                    <div class="row">
                        <div class="col-xs-8 text-right">
                            <label style="margin-bottom: 2px;">
                                Total
                            </label>
                        </div>
                        <div class="col-xs-4 text-right" style="padding-right: 0px;">
                            {{ frappe.utils.fmt_money(doc.total, currency="₹") }}
                        </div>
                    </div>
                    {% if doc.total_discount %}
                    <div class="row">
                        <div class="col-xs-8 text-right">
                            <label style="margin-bottom: 2px;">
                                Total Discount
                            </label>
                        </div>
                        <div class="col-xs-4 text-right" style="padding-right: 0px;">
                            {{ frappe.utils.fmt_money(doc.total_discount, currency="₹") }}
                        </div>
                    </div>
                    {% endif %}
                    <div class="row">
                        <div class="col-xs-8 text-right">
                            <label style="margin-bottom: 2px;">
                                Total Tax
                            </label>
                        </div>
                        <div class="col-xs-4 text-right" style="padding-right: 0px;">
                            {{ frappe.utils.fmt_money(doc.total_tax, currency="₹") }}
                        </div>
                    </div>
                    <div class="row">
                        <div class="col-xs-8 text-right">
                            <label style="margin-bottom: 2px;">Grand Total</label></div>
                        <div class="col-xs-4 text-right" style="padding-right: 0px; font-weight: bold;">
                            {{ frappe.utils.fmt_money(doc.grand_total, currency="₹") }}
                        </div>
                    </div>
                    <div class="row text-right">
                        <span style="font-weight: bold;">In Words:</span> <span>{{ doc.in_words }}</span>
                    </div>
                </div>
            </div>
        </div>
        <div class="row">
            {% set company = frappe.db.get_single_value("Company Settings", "company") %}
            <div class="col-xs-6 text-center" style="border-right:0px solid #666;">
            </div>
            <div class="col-xs-6 text-right">
                <small>For </small>
                <strong>{{ company  }}</strong>
                <br>
                {% set signature = get_user_signature(doc.approved_by) %}
                {% if signature %}
                <div>
                    <img src={{signature}} style="height: 50px;">
                    <br>Authorized Signatory
                </div>
                {% else %}
                <br>
                <br> Authorized Signatory
                {% endif %}
            </div>
        </div>
    </div>
</div>"}]
