css:
th, td{
    border:1px solid gray;
    font-size:14px;
    line-height:1;
}
.no-border{
    border:unset;
}
.border-style{
    border: 1px solid gray; 
}
.border-top-style{
    border: 1px solid gray;
    border-top-left-radius: 10px 10px;
    border-top-right-radius: 10px 10px;
}
.border-bottom-style{
    border: 1px solid gray;
    border-bottom-left-radius: 10px 10px;
    border-bottom-right-radius: 10px 10px 
}
.border-group{
    text-align: center;
    border-bottom: 1px solid gray;
    padding-bottom: 3px;
    width:100%;
}
.font_weight{
    font-weight:600;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading"></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="padding-top:10px;">
	<div class="border-top-style" style="height:50px;text-align: center;">
		<p style="font-size:20px;">Goods Received Note</p>
	</div>
	<div style="width:100%;height:auto;display:flex;">
		<div class="border-style" style="width:33%;padding:5px;">
			From:<br>
			{{doc.supplier_address_display}}
		</div>
		<div class="border-style" style="width:34%;padding:5px;">
			To.<br>
			{{doc.delivery_address_display}}
		</div>
		<div class="border-style" style="width:33%;">
			<div class="border-group" style="height:15px;">Date : {{doc.posting_date}}</div>
			<div class="border-group" style="height:15px;">GRN No: {{doc.name}}</div>
			<div class="border-group" style="height:15px;">
				Vehicle No: 
				{% if doc.vehicle_no %}
					<span>{{doc.vehicle_no}}</span>
				{% else %}
					<span>NA</span>
				{% endif %}
			</div>
			<div class="border-group" style="height:15px;">
				DC No: 
				{% if doc.dc_no %}
					<span>{{doc.dc_no}}</span>
				{% else %}
					<span>NA</span>
				{% endif %}
			</div>
			<div class="border-group" style="height:15px;">Against: {{doc.against}}</div>
			<div style="height:15px;text-align:center;">Against ID: {{doc.against_id}}</div>

		</div>
	</div>
	<div class="border-style" style="text-align: center;">
		Particulars
	</div>
	<div>
		{% set items = get_grn_structure(doc.name) %}
		<table style="width:100%;">
			{% for i in items %}
				<tr>
					{% if i.primary_attribute %}
						<td>
							{% if i.items() %}
								<div style="width: 100%;display:flex;">
									<div style="width: 50%;">Item name:<span class="font_weight">{{i['items'][0]['name']}}</span></div>
									<div style="width: 50%;">Lot : <span class="font_weight">{{i.lot}}</span></div>
								</div>
								<table style="width:100%;">
									{% set primary_values = {} %}
									<tr>
										<td class="font_weight">S.No.</td>
										{% for attr in i.attributes %}
											<td class="font_weight">{{attr}}</td>
										{% endfor %}
										<td class="font_weight">Type</td>
										{% for attr in i.primary_attribute_values %}
											{% set _ = primary_values.update({attr: 0}) %}
											<td class="font_weight">{{attr}}</td>
										{% endfor %}
										<td class="font_weight">Total</td>
									</tr>	
									{% for j in i['items'] %}
										{% for t in j['types']%}
											<tr>
												<td>{{loop.index}}</td>
												{% for attr in i.attributes %}
													<td>
														{% if j.attributes[attr]%}
															{{ j.attributes[attr] }}
														{% endif %}
													</td>
												{% endfor %}
												<td>{{t}}</td>
												{% set total = {"total":0} %}
												{% for attr in j['values'] %}
													{% if j['values'][attr]['types']%}
														{% for ty in j['values'][attr]['types'] %}
															{% if ty == t %}
																<td>
																	{% set attr_qty = primary_values[attr] + j['values'][attr]['types'][ty] %}
																	{% if primary_values.update({attr:attr_qty })%}{% endif %}
																	{% set qty = total["total"] + j['values'][attr]['types'][ty] %}
																	{% if total.update({"total":qty })%}{% endif %}
																	{{j['values'][attr]['types'][ty] | round(3)  }}
																	{% if j.default_uom %}
																		<span>{{ " " + j.default_uom }}</span>
																	{% endif %}
																</td>
															{% endif %}	
														{% endfor %}	
													{% else %}
														<td></td>
													{% endif %}
												{% endfor %}
												<td class="font_weight" >{{total["total"] | round(3) }}</td>
											</tr>
										{% endfor %}	
									{% endfor %}
									<tr>
										<td class="font_weight">Total</td>
										{% for attr in i.attributes %}
											<td></td>
										{% endfor %}
										<td></td>
										{% for attr in i.primary_attribute_values %}
											<td class="font_weight">{{primary_values[attr] | round(3) }}</td>
										{% endfor %}
										<td></td>
									</tr>
								</table>
							{% endif %}
						</td>	
					{% else %}
						<td>
							{% if i.items() %}
							    <div>Lot: <span class="font_weight">{{i.lot}}</span></div>
								<table style="width:100%;">
									<tr>
										<td class="font_weight">S.No.</td>
										<td class="font_weight">Item</td>
										{% for attr in i.attributes %}
											<td class="font_weight">{{attr}}</td>
										{% endfor %}
										<td class="font_weight">Type</td>
										<td class="font_weight">Received qty</td>
									</tr>
									{% set total = {"total":0} %}
									{% for j in i['items'] %}
										{% for t in j['types']%}
											<tr>
												<td>{{ loop.index }}</td>
												<td>{{ j.name }}</td>
												{% for attr in i.attributes %}
													<td>{{ j.attributes[attr] }}</td>
												{% endfor %}
												<td>{{t}}</td>
												<td>
													{% if j['values']["default"]['qty'] > 0 %}
														{% set _ = total["total"] + j['values']["default"]['qty'] %}
														{% if total.update({ "total":_ }) %}{% endif %}
														{{ j['values']["default"]['qty'] | round(3) }}
														{% if j.default_uom %}
															<span>{{ " " + j.default_uom }}</span>
														{% endif %}	
													{% endif %}		
												</td>
											</tr>
										{% endfor %}	
									{% endfor %}
									<tr>
										<td></td>
										<td class="font_weight">Total</td>
										{% for attr in i.attributes %}
											<td></td>
										{% endfor %}
										<td></td>
										<td class="font_weight">{{ total["total"] | round(3) }}</td>
									</tr>
								</table>
							{% endif %}	
						</td>
					{% endif %}
				</tr>	    
			{% endfor %}
		</table>
	</div>
	<div style="height:90px;width:100%;display:flex;">
		<div class="border-style" style="height:90px;width:50%">
			<div  class="font_weight" style="text-align:center;height: 15px;">
				Receiver's Signature
			</div>
			<div style="height:50px;">

			</div>
			<div style="display:flex;height:15px;width:100%;">
				<div  class="font_weight" style="height: 15px;width:50%;text-align: left;padding-left:5px;">
					Name:
				</div>
				<div class="font_weight" style="height: 15px;width:50%;text-align: left;">
					Designation:
				</div>
			</div>
		</div>
		<div class="border-style font_weight" style="height:90px;width:50%">
			<div style="text-align:right;height: 15px;padding-right:15px;">
				For <span class="font_weight">Essdee Knitting Mills Private Limited</span>
			</div>
			<div style="height:50px;">

			</div>
			<div style="height:15px;width:100%;text-align:right;padding-right: 15px;">
				Authorised Signatory
			</div>
		</div>
	</div>
	{% set user_current_time = get_current_user_time() %}
	{% set user = user_current_time[0] %}
	{% set current_time = user_current_time[1] %}
	<div class="border-bottom-style font_weight" style="display: flex;height: 20px;width: 100%;">
		<div style="width: 50%;padding-left: 15px;">
			Print Time: {{current_time}}	
		</div>	
		<div style="width: 50%;padding-left: 15px;">
			Print User : {{user}}
		</div>

	</div>
</div>
"}]
